mapper.select:

		select p.ID,KHZQ,p.DEPT_ID,d.name as DEPT_NAME,ZGXM,NL,XB,CHE_JIAN,BAN_ZU,GANG_WEI,SFZH,XING_JI,ZHDF,YWJN,ZZSJ,AQJX,WCGZ from XJPJ_PINGDINGXINXI p left join sys_dept d on p.dept_id = d.dept_id 

		  		   and p.ID = #{id} 
		  		   and KHZQ = #{khzq} 
		  		   and p.DEPT_ID = #{deptId} 
		  		   and ZGXM = #{zgxm} 
		  		   and NL = #{nl} 
		  		   and XB = #{xb} 
		  		   and CHE_JIAN = #{cheJian} 
		  		   and BAN_ZU = #{banZu} 
		  		   and GANG_WEI = #{gangWei} 
		  		   and SFZH = #{sfzh} 
		  		   and XING_JI = #{xingJi} 
		  		   and ZHDF = #{zhdf} 
		  		   and YWJN = #{ywjn} 
		  		   and ZZSJ = #{zzsj} 
		  		   and AQJX = #{aqjx} 
		  		   and WCGZ = #{wcgz} 



                order by ${sort} ${order}


                order by p.khzq desc,p.ID

select count(*) from XJPJ_PINGDINGXINXI

  		   and ID = #{id} 
  		   and KHZQ = #{khzq} 
  		   and DEPT_ID = #{deptId} 
